import { cardContext } from "./context";

export const Card = () => {
  co
  return (
    <cardContext.Consumer>
      {(value) => {
        return (
          <div className="card" style={{ backgroundColor: value.color }}>
            <h3>{value.text}</h3>
          </div>
        );
      }}
    </cardContext.Consumer>
  );
};
