  const [savedPosts, setSavedPosts] = useState([]);
  const [showSavedList, setShowSavedList] = useState(false);

  return (
    <postContext.Provider
      value={{ savedPosts, setSavedPosts, showSavedList, setShowSavedList }}
    >
      {children}
    </postContext.Provider>
  );
}
